SELECT D.* FROM ( SELECT ROWNUM RN, C.* FROM ( SELECT A.USER_ID, A.EMAIL, A.NIK_NAME, A.ADDR, A.USER_PHONE, TO_CHAR(A.RDATE,'YYYY-MM-DD') RDATE, TO_CHAR(A.UDATE,'YYYY-MM-DD') UDATE FROM MEMBER A, MEM_STATUS B WHERE A.user_id = b.user_id AND B.MEM_STATE_CODE = 1
AND A.NIK_NAME LIKE '%${word}%'
ORDER BY A.RDATE DESC ) C ) D WHERE RN BETWEEN #{startIndex} AND #{endIndex}

SELECT D.* FROM ( SELECT ROWNUM RN, C.* FROM ( SELECT A.USER_ID, A.EMAIL, A.NIK_NAME, A.ADDR, A.USER_PHONE, B.REASON, B.MEM_STATE_CODE, TO_CHAR(A.RDATE,'YYYY-MM-DD') RDATE, TO_CHAR(A.UDATE,'YYYY-MM-DD') UDATE FROM MEMBER A, MEM_STATUS B WHERE A.user_id = b.user_id AND B.MEM_STATE_CODE IN (2, 3)
AND A.NIK_NAME LIKE '%${word}%'
ORDER BY A.UDATE DESC ) C ) D WHERE RN BETWEEN #{startIndex} AND #{endIndex}

SELECT D.* FROM ( SELECT ROWNUM RN, C.* FROM ( SELECT A.USER_ID, A.EMAIL, A.NIK_NAME, A.ADDR, A.USER_PHONE, B.REASON, B.MEM_STATE_CODE, TO_CHAR(A.RDATE,'YYYY-MM-DD') RDATE, TO_CHAR(A.UDATE,'YYYY-MM-DD') UDATE, TO_CHAR(sysdate-3,'YYYY-MM-DD') threeago FROM MEMBER A, MEM_STATUS B WHERE A.user_id = b.user_id AND B.MEM_STATE_CODE = 5
AND A.NIK_NAME LIKE '%${word}%'
ORDER BY A.UDATE DESC ) C ) D WHERE RN BETWEEN #{startIndex} AND #{endIndex}

SELECT b.* FROM (SELECT ROWNUM rn, a.* FROM (SELECT unq, title, category, content, to_char(rdate,'yyyy-mm-dd') rdate, decode(status,'N','답변대기','답변완료') status, nik_name FROM INQUIRY_BOARD WHERE 1 = 1
AND STATUS like '%${field}%'
ORDER BY unq DESC ) a ) b WHERE rn BETWEEN #{startIndex} AND #{endIndex}
