mapper.select:
SELECT pro_code, seller, pro_category_code, title, keyword, addr, price, content, rdate, udate, status, hits, nvl(buyer, '0') as buyer, refund, nego, ( select nik_name from member where user_id = p.seller) seller_nik, nvl(buyer_nik, '0') as buyer_nik, imgs
FROM product p
WHERE pro_code = ${proCode}

SELECT pro_code, seller, pro_category_code, title, keyword, addr, price, content, rdate, udate, seller_nik, imgs
FROM product
WHERE pro_code = ${proCode}

SELECT user_id, addr
FROM member
WHERE user_id = ${seller}
