mapper.select:
SELECT count(*) FROM keyword
	WHERE srch_ip=#{srchIp} 
		AND KEYWORD=#{keyword}

mapper.insert:
INSERT INTO keyword(
keyword
, rdate
, srch_date
, srch_ip
) VALUES (
#{keyword}
, sysdate
, sysdate
, #{srchIp}
)

mapper.update:
UPDATE keyword SET srch_date=sysdate
	WHERE srch_ip=#{srchIp} 
		AND keyword=#{keyword}
