mapper.select:
SELECT pro_code, decode(status,1,'판매중',2,'예약중', 0, '거래완료') status, title, price, addr, imgs, (select CASE WHEN USER_LEVEL >= 70 THEN '신뢰회원' WHEN USER_LEVEL >= 40 THEN '일반회원' ELSE '주의회원' END FROM member WHERE user_id = p.seller) as user_level FROM product p
WHERE pro_category_code = ${proCategoryCode}
ORDER BY rdate DESC

SELECT pro_code, decode(status,1,'판매중',2,'예약중', 0,'거래완료') status, seller, pro_category_code, title, keyword, addr, price, content, rdate, udate, status, hits, nvl(buyer, '0') as buyer, refund, nego, ( select nik_name from member where user_id = p.seller) seller_nik, nvl(buyer_nik, '없음') as buyer_nik, imgs, (select CASE WHEN USER_LEVEL >= 70 THEN '신뢰회원' WHEN USER_LEVEL >= 40 THEN '일반회원' ELSE '주의회원' END FROM member WHERE user_id = p.seller) as user_level
FROM product p
WHERE pro_code = ${proCode}

SELECT pro_code, seller, buyer, pro_category_code, title, price, status, ( select nik_name from member where user_id = p.seller) seller_nik, nvl(buyer_nik, '없음') as buyer_nik, imgs
FROM product p
WHERE seller = ${seller}

SELECT pro_code, seller, buyer, pro_category_code, title, price, status, ( select nik_name from member where user_id = p.seller) seller_nik, nvl(buyer_nik, '없음') as buyer_nik, imgs
FROM product p
WHERE buyer = ${buyer}

SELECT pro_code, decode(status,1,'판매중',2,'예약중', 0, '거래완료') status, seller, pro_category_code, title, keyword, addr, price, content, rdate, udate, status, hits, nvl(buyer, '0') as buyer, refund, nego, ( select nik_name from member where user_id = p.seller) seller_nik, nvl(buyer_nik, '0') as buyer_nik, imgs, (select CASE WHEN USER_LEVEL >= 70 THEN '신뢰회원' WHEN USER_LEVEL >= 40 THEN '일반회원' ELSE '주의회원' END FROM member WHERE user_id = p.seller) as user_level
FROM product p
WHERE pro_code = ${proCode}

SELECT user_id, addr
FROM member
WHERE user_id = ${seller}

mapper.update:
UPDATE product
	SET hits = hits + 1
WHERE pro_code = ${proCode}

UPDATE product
	SET
		status = ${status}
WHERE pro_code = ${proCode}
